import React, { useEffect, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import '../../styles/Home.css';

const events = [
  {
    date: '05 June',
    title: 'HALO NIGHT PARTY',
    subtitle: 'ROCK THE CITY',
    image: '/images/event1.jpg',
  },
  {
    date: '12 June',
    title: 'HOT CLUB NIGHTS',
    subtitle: 'THE BEST CLUB MUSIC',
    image: '/images/event2.jpg',
  },
  {
    date: '23 June',
    title: 'DNB DANCE',
    subtitle: 'CARNIVAL ON BEATS',
    image: '/images/event3.jpg',
  },
];

function Home() {
  const sectionsRef = useRef([]);
  const cardsRef = useRef([]);
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
      }
    );
    sectionsRef.current.forEach((section) => {
      if (section) observer.observe(section);
    });
    cardsRef.current.forEach((card) => {
      if (card) observer.observe(card);
    });
    return () => {
      sectionsRef.current.forEach((section) => {
        if (section) observer.unobserve(section);
      });
      cardsRef.current.forEach((card) => {
        if (card) observer.unobserve(card);
      });
    };
  }, []);

  const services = [
    {
      title: 'Event Organizing',
      description: 'Transform your special occasions into unforgettable experiences.',
      icon: 'ðŸŽ‰'
    },
    {
      title: 'Technical Services',
      description: 'Elevate your event with our comprehensive technical services.',
      icon: 'ðŸ”§'
    },
    {
      title: 'Event Staffing',
      description: 'Our dedicated event staffing solutions guarantee exceptional service.',
      icon: 'ðŸ‘¥'
    }
  ];

  const categories = [
    'Music', 'Nightlife', 'Holidays', 'Visual Arts',
    'Health', 'Hobbies', 'Business', 'Food & Drink'
  ];

  const nextSlide = () => setCurrent((prev) => (prev + 1) % events.length);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + events.length) % events.length);

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-image-section">
        <div className="hero-image-bg" />
        <div className="hero-overlay">
          <div className="hero-date">14<br />June</div>
          <div className="hero-main-text">
            <div className="hero-event-title">MIDNIGHT <span className="hero-event-sub">saturday</span></div>
            <div className="hero-artist">ADRIANA GONZARES</div>
            <div className="hero-location">250 Compton Blvd, Party Club, Houston TX 658 555 541</div>
          </div>
        </div>
      </section>

      {/* Coming Soon Event Slider */}
      <section className="coming-soon-section">
        <h2 className="coming-soon-title">COMING SOON</h2>
        <div className="event-slider">
          <button className="slider-arrow left" onClick={prevSlide}>&lt;</button>
          <div className="event-cards">
            {events.map((event, idx) => (
              <div key={idx} className={`event-card${idx === current ? ' active' : ''}`}> 
                <div className="event-card-img" style={{ backgroundImage: `url(${event.image})` }} />
                <div className="event-card-date">{event.date}</div>
                <div className="event-card-title">{event.title}</div>
                <div className="event-card-subtitle">{event.subtitle}</div>
              </div>
            ))}
          </div>
          <button className="slider-arrow right" onClick={nextSlide}>&gt;</button>
        </div>
      </section>

      {/* Services Section */}
      <section className="services" ref={(el) => (sectionsRef.current[1] = el)}>
        <h2>Our Services</h2>
        <div className="services-grid">
          {services.map((service, index) => (
            <div
              key={index}
              className="service-card"
              ref={(el) => (cardsRef.current[index] = el)}
            >
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories" ref={(el) => (sectionsRef.current[2] = el)}>
        <h2>Event Categories</h2>
        <div className="categories-grid">
          {categories.map((category, index) => (
            <div
              key={index}
              className="category-card"
              ref={(el) => (cardsRef.current[index + services.length] = el)}
            >
              <h3>{category}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Experience Section */}
      <section className="experience" ref={(el) => (sectionsRef.current[3] = el)}>
        <div className="experience-content">
          <h2>Experience</h2>
          <p>With years of industry expertise, we understand the intricacies of event planning and execution. Our diverse portfolio spans a wide range of events, equipping us to navigate challenges and deliver seamless experiences.</p>
        </div>
      </section>
    </div>
  );
}

export default Home;
