import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

function Home() {
  const services = [
    {
      title: 'Event Organizing',
      description: 'Transform your special occasions into unforgettable experiences.',
      icon: 'ðŸŽ‰'
    },
    {
      title: 'Technical Services',
      description: 'Elevate your event with our comprehensive technical services.',
      icon: 'ðŸ”§'
    },
    {
      title: 'Event Staffing',
      description: 'Our dedicated event staffing solutions guarantee exceptional service.',
      icon: 'ðŸ‘¥'
    }
  ];

  const categories = [
    'Music', 'Nightlife', 'Holidays', 'Visual Arts',
    'Health', 'Hobbies', 'Business', 'Food & Drink'
  ];

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Welcome to River & Sky Event Management!</h1>
          <p>Your trusted partner for remarkable events in the UAE.</p>
          <div className="hero-buttons">
            <Link to="/contact" className="cta-button">Contact Us</Link>
            <Link to="/services" className="secondary-button">Our Services</Link>
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section className="services">
        <h2>Our Services</h2>
        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories">
        <h2>Event Categories</h2>
        <div className="categories-grid">
          {categories.map((category, index) => (
            <div key={index} className="category-card">
              <h3>{category}</h3>
            </div>
          ))}
        </div>
      </section>

      {/* Experience Section */}
      <section className="experience">
        <div className="experience-content">
          <h2>Experience</h2>
          <p>With years of industry expertise, we understand the intricacies of event planning and execution. Our diverse portfolio spans a wide range of events, equipping us to navigate challenges and deliver seamless experiences.</p>
        </div>
      </section>
    </div>
  );
}

export default Home;
